ll power(ll base, int exp){
    ll res = 1;
    while(exp--){
        res = (res * base ) % MOD;
    }
    return res;
}

int main(){
    ll n, k;
    cin >> n >> k;

    ll res = cykl[k] * (n / LEOMOD);
    n = n % LEOMOD;

    res += st[n / FSTMOD][k];
    leo[2] = lst[n / FSTMOD][0];
    leo[0] = lst[n / FSTMOD][1];
    n %= FSTMOD;
    if(n > 0){
        res += power(leo[0], k);
        res %= MOD;
    }
	cout<<n<<endl;
    for(int i = 1; i < n; i++){
		cout<<leo[i%3]<<" "<<leo[(i+1)%3] <<" "<<" "<<leo[(i+2)%3]<<endl;
        leo[i%3] = leo[(i+1)%3] + leo[(i+2)%3] + 1;
        while(leo[i%3] >= MOD) leo[i%3] -= MOD;

        res += power(leo[i%3], k);
        res %= MOD;
    }
    printf("%09d\n", res);

}
